/* eslint-disable no-restricted-globals */
import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();

    const {target} = event

    this.state = {
      nfts: [],
    };
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((response) => response.json())
      .then((users) =>
        this.setState(
          () => {
            return { nfts: users };
          },
          () => {
            console.log(this.state);
          }
        )
      );
  }

  render() {
    return (
      <div className='App'>
        <input
          className='search-box'
          type='search'
          placeholder='Search your NFT...'
          onChange={(event) => {
            this.state.nfts.filter((nft) => {
              if (nft.name.includes(event.target.value)) {
                return (
                  <div key={nft.id}>
                    <h1>{nft.name}</h1>
                  </div>
                );
              }
            });
          }}
        />

        {this.state.nfts.map((nft) => {
          return (
            <div key={nft.id}>
              <h1>{nft.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
