/* eslint-disable no-restricted-globals */
import { Component } from 'react';

import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      nfts: [],
      searchField: ''
    };
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((response) => response.json())
      .then((users) =>
        this.setState(
          () => {
            return { nfts: users };
          },
          () => {
            console.log(this.state);
          }
        )
      );
  }

  render() {

    const filteredNfts = this.state.nfts.filter((nft) => {
      return nft.name.toLowerCase().includes(searchString);
    });


    return (
      <div className='App'>
        <input
          className='search-box'
          type='search'
          placeholder='Search your NFT...'
          onChange={(event) => {
            console.log(event.target.value);
            const searchString = event.target.value.toLowerCase();


            this.setState(() => {
              return { searchField: filteredNfts };
            });
          }}
        />

        {this.state.nfts.map((nft) => {
          return (
            <div key={nft.id}>
              <h1>{nft.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
